SET NAMES UTF8;
DROP DATABASE IF EXISTS glen;
CREATE DATABASE IF NOT EXISTS glen CHARSET=UTF8;
USE glen;
#首页数据表
#轮播图
CREATE TABLE glen_index_carousel(
    id INT PRIMARY KEY AUTO_INCREMENT,
    src VARCHAR(128),
    title VARCHAR(64),
    alt VARCHAR(64)
);
#产品
CREATE TABLE glen_index_products(
    id INT PRIMARY KEY AUTO_INCREMENT,
    title VARCHAR(64),
    details VARCHAR(128),
    img VARCHAR(128),
    alt VARCHAR(64),
    href VARCHAR(64)
);
#热门产品
CREATE TABLE glen_index_hotpro(
    id INT PRIMARY KEY AUTO_INCREMENT,
    title VARCHAR(64),
    img VARCHAR(128),
    alt VARCHAR(64),
    href VARCHAR(64)
);
#产品类别列表
CREATE TABLE glen_product_family(
    fid INT PRIMARY KEY AUTO_INCREMENT,
    fname VARCHAR(32)
);
#产品列表
CREATE TABLE glen_products(
    pid INT PRIMARY KEY AUTO_INCREMENT,
    fid INT,
    title VARCHAR(128),
    subtitle VARCHAR(128),
    details TEXT
);
#产品详情放大镜图片列表
CREATE TABLE glen_products_pic(
    id INT PRIMARY KEY AUTO_INCREMENT,
    pid INT,
    pic VARCHAR(128)
);
#新闻列表
CREATE TABLE glen_news(
    id INT PRIMARY KEY AUTO_INCREMENT,
    title VARCHAR(128),
    date DATE,
    detail TEXT
);
#产品知识列表
CREATE TABLE glen_info(
    id INT PRIMARY KEY AUTO_INCREMENT,
    title VARCHAR(128),
    date DATE,
    detail TEXT
);
#订阅信息接收统计表
CREATE TABLE glen_email(
    id INT PRIMARY KEY AUTO_INCREMENT,
    email VARCHAR(128),
    date DATE
);
#反馈信息接收统计表
CREATE TABLE glen_feedback(
    id INT PRIMARY KEY AUTO_INCREMENT,
    email VARCHAR(128),
    content VARCHAR(255),
    date DATE
);
#询价信息接收统计表
CREATE TABLE glen_inquery(
    id INT PRIMARY KEY AUTO_INCREMENT,
    uname VARCHAR(64),
    email VARCHAR(128),
    phone VARCHAR(32),
    company VARCHAR(128),
    title VARCHAR(128),
    content VARCHAR(255),
    date DATE
);