SET NAMES UTF8;
DROP DATABASE IF EXISTS glen;
CREATE DATABASE IF NOT EXISTS glen CHARSET=UTF8;
USE glen;
#首页数据表
#轮播图
CREATE TABLE glen_index_carousel(
    id INT PRIMARY KEY AUTO_INCREMENT,
    src VARCHAR(128),
    title VARCHAR(64),
    alt VARCHAR(64)
);
#产品，
#待添加href指向产品详情页
CREATE TABLE glen_index_products(
    id INT PRIMARY KEY AUTO_INCREMENT,
    title VARCHAR(64),
    details VARCHAR(128),
    img VARCHAR(128),
    alt VARCHAR(64)
);
#热门产品
#待添加href指向产品详情页
CREATE TABLE glen_index_hotpro(
    id INT PRIMARY KEY AUTO_INCREMENT,
    title VARCHAR(64),
    img VARCHAR(128),
    alt VARCHAR(64)
);
#产品类别列表
CREATE TABLE glen_product_family(
    fid INT PRIMARY KEY AUTO_INCREMENT,
    fname VARCHAR(32)
);
#产品列表
CREATE TABLE glen_products(
    pid INT PRIMARY KEY AUTO_INCREMENT,
    fid INT,
    title VARCHAR(128),
    subtitle VARCHAR(128),
    details TEXT
);
#产品详情放大镜图片列表
CREATE TABLE glen_products_pic(
    id INT PRIMARY KEY AUTO_INCREMENT,
    pid INT,
    pic VARCHAR(128)
);
